package com.example.goals.listviewsignalandmultiplechoice;import android.app.ActivityManager;import android.app.Application;import android.app.Service;import android.content.Context;import android.content.pm.PackageInfo;import android.content.pm.PackageManager;import com.example.goals.listviewsignalandmultiplechoice.util.LogUtil;import java.util.List;/** * Created by huyongqiang on 2017/3/29. */public class MyApp extends Application {    private static MyApp instance;    public static MyApp getInstance() {        return instance;    }    @Override    public void onCreate() {        super.onCreate();        instance = this;    }    private String test = null;    public boolean isTest() {        if (test == null) {            try {                PackageInfo info = getPackageManager().getPackageInfo(                        this.getPackageName(), PackageManager.GET_META_DATA);                test = info.applicationInfo.metaData.getString("APP_TEST");                String channel = info.applicationInfo.metaData.getString("UMENG_APPKEY");                String changName = info.applicationInfo.metaData.getString("UMENG_CHANNEL");                LogUtil.d("AppInstance", "channel," + channel + ",channelName," + changName);            } catch (PackageManager.NameNotFoundException e) {                e.printStackTrace();            }        }        return "yes".equals(test);    }}