package com.example.goals.listviewsignalandmultiplechoice.activity;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.support.design.widget.FloatingActionButton;import android.support.design.widget.Snackbar;import android.support.v7.widget.Toolbar;import android.view.View;import com.example.goals.listviewsignalandmultiplechoice.R;public class LaunchActivity extends BaseActivity {    public int count = 0;    /**     * 冷启动，闪屏，未点击广告     */    public final static int SPLASH_TYPE_LAUNCH = 0;    /**     * 冷启动，闪屏，点击广告，跳转前     */    private final static int SPLASH_TYPE_LAUNCH_AD_BEFORE = 1;    /**     * 冷启动，闪屏，点击广告，跳转后     */    private final static int SPLASH_TYPE_LAUNCH_AD_AFTER = 2;    /**     * 热启动，触发闪屏机制，闪屏，未点击广告     */    public final static int SPLASH_TYPE_COMMON = 12;    /**     * 热启动，触发闪屏机制，闪屏，点击广告，跳转前     */    private final static int SPLASH_TYPE_COMMON_AD_BEFORE = 13;    /**     * 热启动，触发闪屏机制，闪屏，点击广告，跳转后     */    private final static int SPLASH_TYPE_COMMON_AD_AFTER = 14;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_launch);        Toolbar toolbar = (Toolbar) findViewById(R.id.toolbar);        setSupportActionBar(toolbar);        FloatingActionButton fab = (FloatingActionButton) findViewById(R.id.fab);        fab.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                Snackbar.make(view, "Replace with your own action", Snackbar.LENGTH_LONG)                        .setAction("Action", null).show();            }        });    }    public static void startAct(Activity activity, int splashType) {        Intent intent = new Intent(activity, LaunchActivity.class);        intent.putExtra("splash_type", splashType);        activity.startActivity(intent);    }}